mapper.select:
select *
from
(
    select rownum as RNUM, A.*
    from
    (
        select * from board 

        order by no desc
    )A
)
where RNUM > #{firstRecordIndex} 

and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update board 
set name = #{name}, title = #{title},
	email = #{email}, content = #{content}
where no = #{no}
